.data.textmain:addi $r29, $r0, 0x00000DFFaddi $r3, $r0, 0x0000012CmainStart:addi $r2, $r0, 0addi $r1, $r0, 1jal scrolladdi $r2, $r0, 0addi $r1, $r0, 2jal scrolladdi $r2, $r0,0addi $r1, $r0, 4jal scrollj mainStarthaltscroll:addi $r29, $r29, -1sw $r31, 0($r29)scrollStart:sw $r1, 0($r2)addi $r2, $r2, 1jal delayblt $r2, $r3, scrollStartscrollFinish:lw $r31, 0($r29)addi $r29, $r29, 1jr $r31delay:addi $r5, $r0, 0x0000FFFFaddi $r4, $r0, 0delayStart:addi $r4, $r4, 1nopnopblt $r4, $r5, delayStartdelayFinish:jr $r31